import React, { useState, useEffect } from 'react';
import Card from '@mui/material/Card';
import CardActions from '@mui/material/CardActions';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import { Link } from 'react-router-dom';

export default function Home() {
  const [data, setData] = useState([]);
  const [input, setInput] = useState({});
  // const [title, setTitle] = useState("");
  // const [poster, setPoster] = useState("");
  const [movie_list, setMovie_list] = useState([])
  useEffect(() => {
    async function fetchData() {
      const response = await fetch('http://127.0.0.1:5000/home');
      const result = await response.json();
      setData(result);
    }
    fetchData();
  }, []);

  const getMovies = async (input) => {

    const res = await fetch('http://127.0.0.1:5000/moviename', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(input)
    })
    .then(res => res.json())
      .then(data => {
        console.log(data)      
    })
    
  }
  
  const handleClick = item => {
      setInput({ 'title1': item });
      getMovies(input)
      
  };
 


  const baseURL = "https://image.tmdb.org/t/p/w500/"
  return (
    <div>
      {data.map(item => (
        <Card sx={{ maxWidth: 345 }} key={item.id}>
        <CardMedia
          sx={{ height: 140 }}
          image={item.poster}
          title={item.name}
        />
        <CardContent>
          <Typography gutterBottom variant="h5" component="div">
          {item.name}
          </Typography>
        </CardContent>
        <CardActions>
        <Button size="small" onClick={() => handleClick(item.name)}>
              Recommendations
          </Button>
      </CardActions>
      </Card>
      
    ))}
    </div>
    
  );
}
